export default class SyswideCas {
  /**
    Adds custom Certificate Authorities from the specified directories or files.

    @param dirs - A single directory/file path or an array of paths, or a comma-separated string of paths.
  */
  static addCAs(dirs: string | string[]): void

  /**
    Adds certificate authority to list of root certificate authorities.
    @param file
  */
  static addDefaultCA(file: string): void
}
